import React, { Component } from 'react';
import './animation.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Row, Col
      } from 'reactstrap';

import QR from './images/cardQR.png';
import Social from './images/social.png';
import Email from './images/email.png';
import Barcode from './images/barcode.png';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

export default class Contact extends Component  {

   mailFields : {};




  updateFields() {
      let formInputs: HTMLCollection = document.getElementsByClassName('form-control');
      this.mailFields.email = formInputs[0].value;
      this.mailFields.subject = formInputs[1].value;
      this.mailFields.body = formInputs[2].value;
      console.log(this.mailFields);
    };

    sendEmail() {
      window.location.href = `mailto:scott.lau.stubbs@gmail.com?subject=${this.mailFields.subject}&body=${this.mailFields.body}&cc=${this.mailFields.email}`;
    };


  render() {
      return (

        <div>
        <h1 className="contactStyle">Get In Touch</h1>

        <Row>
          <Col className="center">
            <Row>

              <img className="center"
                src={Barcode}
                height = "50%"
                width = "50%"
                alt = "scan-barcode"
              />
            </Row>

          </Col>
          <Col className="center">
            <img className="center"
              src={Email}
              height = "50%"
              width = "50%"
              alt ="send-email"
            />
          </Col>
          <Col className="center">


            <img className="center"
              src={Social}
              height = "50%"
              width = "50%"
              alt ="social"
            />

          </Col>
        </Row>

        <Row>
          <Col className="center">
            <img
              src={QR}
              height = "75%"
              width = "50%"
              alt ="QR"
            />
          </Col>
          <Col className="center">
          <a rel="noopener noreferrer"
              className="contactStyle" href="mailto:scott.lau.stubbs@gmail.com" >
              <h3 className="contactStyle">Gmail</h3>
          </a>
          </Col>
          <Col className="center">
          <a rel="noopener noreferrer"
              className="contactStyle" href="https://github.com/mrscottstubbs" >
              <h3 className="contactStyle">GitHub</h3>
          </a>
          <a rel="noopener noreferrer"
              className="contactStyle" href="https://www.linkedin.com/in/scott-stubbs/" >
              <h3 className="contactStyle">LinkedIn</h3>
          </a>
          </Col>
        </Row>

        <div style={{width: '70%', margin: 'auto'}}>
          <Form onChange = {() => this.updateFields()}>
            <Form.Group controlId="contactForm.email">
              <Form.Label>Who I am:</Form.Label>
              <Form.Control type="email" placeholder="your.email@example.com" />
            </Form.Group>
            <Form.Group controlId="contactForm.subject">
              <Form.Label>Why I'm emailing you:</Form.Label>
              <Form.Control as="select">
                <option>Congratulate you on the Site ! </option>
                <option>I'll explain in below</option>
              </Form.Control>
            </Form.Group>
            <Form.Group controlId="contactForm.body">
              <Form.Label>What I have to say:</Form.Label>
              <Form.Control as="textarea" />
            </Form.Group>
            <Button variant="primary" onClick={() => this.sendEmail()}>Submit</Button>
          </Form>
        </div>

        </div>
      );
  }
}
