import React, { Component } from 'react'
import enigma from './images/enigma.jpg';
import vr from './images/vr.jpg';
import bench from './images/bench.jpg';
import lift from './images/lift.jpg';
import {Row, Col} from 'reactstrap'
import classNames from 'classnames';
import AnimatedCollapsible from './AnimatedCollapsible.js';

const conf= {
    name: 'Enigma Conference',
    head: "Cali",
    bio: "Had the opportunity to meet and learn from some of the leading companies in the industry.",
    pic: enigma
}

const learn= {
  name: 'Vr',
  head: "College",
  bio: "Knowledge.",
  pic: vr
}

const chill= {
  name: 'Sitting',
  head: "Sit",
  bio: "ok",
  pic: bench
}

const hofStrong= {
  name: 'Weight Lifting',
  head: "Non Nerd Stuff",
  bio: "Cool guy things",
  pic: lift
}

const hack= {
  name: 'Estee Lauder',
  head: "hack",
  bio: "hacking",
  pic: lift
}

var activity = [conf, learn, chill, hofStrong, hack];

export default class Fun extends Component {

    createCarousel = () => {
      const schoolText = {
        border: "1px solid black",
        backgroundColor: "rgba(255,255,255,0.5)",
        padding: "8%",
        borderRadius: "4px",
      }

        let elements = [];


        for(let i =0; i<activity.length; i++){
            elements.push(
             <Col data-aos="zoom-in-up">
                <div style = {{width:"100%", paddingTop:"1em"}}>
                  <img
                  className = {classNames("rounded-image", "shadow-img")}
                  src = {activity[i].pic}
                  alt = {activity[i].name}
                  width="75%"
                  />
                </div>
                <div style={schoolText}>
                 <h4 style={{minHeight:"1em", padding:"3%"}}>{activity[i].head}</h4>
                 <hr />
                 <h5><AnimatedCollapsible text={activity[i].bio}></AnimatedCollapsible></h5>
                </div>
              </Col>
            )
          }
        return elements;
    }

  render() {

    return (
      <div className="container">
        <Row style={{display:"flex"}}>
          {this.createCarousel()}
        </Row>
      </div>




    )
  }
}
