import React, { Component } from 'react';
import './animation.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Container, Row, Col
      } from 'reactstrap';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import QR from './images/cardQR.png';
import Social from './images/social.png';
import Email from './images/email.png';
import Barcode from './images/barcode.png';

export default class Contact extends Component  {

  constructor(props) {
    super(props)
    this.state = {
      formValues: {}
    }
  }

  handleChange(event) {
    event.preventDefault();
    let formValues = this.state.formValues;
    let name = event.target.name;
    let body = event.target.body;
    let select = event.target.select;
    let subject = event.target.subject;
    let value = event.target.value;

    formValues[name] = value;

    this.setState({formValues})
  }

  handleSubmit(event) {
    window.location.href = `mailto:scott.lau.stubbs@gmail.com?subject=${this.state.formValues["select"]}&body=${this.state.formValues["body"]}&from=${this.state.formValues["name"]}`;
  }




  render() {
      return (

        <div>
        <h1 className="contactStyle">Get In Touch</h1>

        <Row>
          <Col className="center">
            <Row>

              <img className="center"
                src={Barcode}
                height = "50%"
                width = "50%"
                alt = "scan-barcode"
              />
            </Row>

          </Col>
          <Col className="center">
            <img className="center"
              src={Email}
              height = "50%"
              width = "50%"
              alt ="send-email"
            />
          </Col>
          <Col className="center">


            <img className="center"
              src={Social}
              height = "50%"
              width = "50%"
              alt ="social"
            />

          </Col>
        </Row>

        <Row>
          <Col className="center">
            <img
              src={QR}
              height = "75%"
              width = "50%"
              alt ="QR"
            />
          </Col>
          <Col className="center">
          <a rel="noopener noreferrer"
              className="contactStyle" href="mailto:scott.lau.stubbs@gmail.com" >
              <h3 className="contactStyle">Gmail</h3>
          </a>
          </Col>
          <Col className="center">
          <a rel="noopener noreferrer"
              className="contactStyle" href="https://github.com/mrscottstubbs" >
              <h3 className="contactStyle">GitHub</h3>
          </a>
          <a rel="noopener noreferrer"
              className="contactStyle" href="https://www.linkedin.com/in/scott-stubbs/" >
              <h3 className="contactStyle">LinkedIn</h3>
          </a>
          </Col>
        </Row>

        <Container>
        <Form onSubmit={this.handleSubmit.bind(this)}>
          <Label> Who are you? :
            <Input type="text" name="name" placeholder="Name" value={this.state.formValues["name"]} onChange={this.handleChange.bind(this)} />
          </Label><br />
          <Label> Why are you emailing me? :
            <Input type="select" name="select" placeholder="Email" value={this.state.formValues["email"]} onChange={this.handleChange.bind(this)}>
              <option>Congratulating you on the site :) </option>
              <option>I'll explain in the Email</option>
            </Input>
          </Label><br />
          <Label> Body :
            <Input type="text" name="body" placeholder="Body" value={this.state.formValues["body"]} onChange={this.handleChange.bind(this)}/>
          </Label><br />
          <Input variant="primary" onClick={() => this.handleSubmit()} type="submit" value="Submit" />
        </Form>
        </Container>
        </div>
      );
  }
}
