import React, { Component } from 'react'
import lift from './images/lift.jpg';
import vr from './images/vr.jpg';
import {Row, Col} from 'reactstrap'
import classNames from 'classnames';
import AnimatedCollapsible from './AnimatedCollapsible.js';

const hofStrong= {
  name: 'Weight Lifting',
  head: "Non Nerd Stuff",
  bio: "Cool guy things",
  pic: lift
}

const tech= {
  name: 'New Tech',
  head: "testing",
  bio: "Exploring new things",
  pic: vr
}

var activities = [hofStrong, tech];

/* The Carousel component renders a DOM element that contains all of the team's information.
 *
 * It does this dynamically with createCarousel(), which uses an array of JavaScript objects
 *  and iterates over them to populate sub-columns with our data. By calling on the right
 *  key:value pair, data gets written to its respective styled tag.
 * After a column is generated, it is given the mobile responsive property of resizing its
 *  width depending on browser size. Each column then gets pushed to the page.
 */

export default class extraCurricular extends Component {

    createCarousel = () => {
      const schoolText = {
        border: "1px solid black",
        backgroundColor: "rgba(255,255,255,0.5)",
        padding: "8%",
        borderRadius: "4px",
      }

        let elements = [];


        for(let i = 0; i < activities.length; i++) {
            let duration = 900 + i * 450; // Creates staircase effect
            elements.push(
             <Col xs={12} sm={8} md={5} key={i} data-aos="zoom-in-up" data-aos-duration={duration}>
                <div style = {{width:"100%", paddingTop:"1em"}}>
                  <img
                  className = {classNames("rounded-image", "shadow-img")}
                  src = {activities[i].pic}
                  alt = {activities[i].name}
                  width="75%"
                  />
                </div>
                <br />
                <h3 style={{minHeight:"4em"}}>{activities[i].name}</h3>
                <div style={schoolText}>
                 <h4 style={{minHeight:"4em", padding:"3%"}}>{activities[i].head}</h4>
                 <hr />
                 <h5><AnimatedCollapsible text={activities[i].bio}></AnimatedCollapsible></h5>
                </div>
              </Col>
            )
        }
        return elements;
    }

  render() {

    return (
      <div className="container">
        <Row style={{display:"flex"}}>
          {this.createCarousel()}
        </Row>
      </div>
    )
  }
}
