import React, { Component } from 'react';
import './animation.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
  Row, Col
      } from 'reactstrap';

import QR from './images/cardQR.png';
import Social from './images/social.png';
import Email from './images/email.png';
import Barcode from './images/barcode.png';
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

export default class Contact extends Component  {

    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {
        this.setState({value: event.target.value});
      }

      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }



  render() {
      return (

        <div>
        <h1 className="contactStyle">Get In Touch</h1>

        <Row>
          <Col className="center">
            <Row>

              <img className="center"
                src={Barcode}
                height = "50%"
                width = "50%"
                alt = "scan-barcode"
              />
            </Row>

          </Col>
          <Col className="center">
            <img className="center"
              src={Email}
              height = "50%"
              width = "50%"
              alt ="send-email"
            />
          </Col>
          <Col className="center">


            <img className="center"
              src={Social}
              height = "50%"
              width = "50%"
              alt ="social"
            />

          </Col>
        </Row>

        <Row>
          <Col className="center">
            <img
              src={QR}
              height = "75%"
              width = "50%"
              alt ="QR"
            />
          </Col>
          <Col className="center">
          <a rel="noopener noreferrer"
              className="contactStyle" href="mailto:scott.lau.stubbs@gmail.com" >
              <h3 className="contactStyle">Gmail</h3>
          </a>
          </Col>
          <Col className="center">
          <a rel="noopener noreferrer"
              className="contactStyle" href="https://github.com/mrscottstubbs" >
              <h3 className="contactStyle">GitHub</h3>
          </a>
          <a rel="noopener noreferrer"
              className="contactStyle" href="https://www.linkedin.com/in/scott-stubbs/" >
              <h3 className="contactStyle">LinkedIn</h3>
          </a>
          </Col>
        </Row>

        <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>


        </div>
      );
  }
}
